SELECT * FROM slackbot.open_dialog
  WHERE $body=(SELECT {
    'dialog' : {
      'title': 'Save it to ClipIt!',
      'callback_id': 'clipit',
      'submit_label': 'ClipIt',
      'elements': [
         {
           'label': 'Message Text',
           'type': 'textarea',
           'name': 'message',
         },
         {
           'label': 'Posted by',
           'type': 'text',
           'name': 'send_by',
         },
         {
           'label': 'Importance',
           'type': 'select',
           'name': 'importance',
           'value': 'Medium',
           'options': [
             { 'label': 'High', 'value': 'High' },
             { 'label': 'Medium', 'value': 'Medium' },
             { 'label': 'Low', 'value': 'Low' }
           ],
         },
      ]
    },
    'trigger_id' : @trigger_id
  })

