tql.tql:
SELECT * FROM slackbot.post_chat_message
  WHERE $body=(SELECT {
    'channel': @channel,
    'as_user': true,
    'text': 'Helpdesk ticket created!',
    'attachments': [
      {
        'title': @email,
        -- Get this from the 3rd party helpdesk system
        'title_link': @url,
        'fields': [
          {
            'title': 'Title',
            'value': @title,
          },
          {
            'title': 'Description',
            'value': @description,
          },
          {
            'title': 'Status',
            'value': 'Open',
            'short': true,
          },
          {
            'title': 'Urgency',
            'value': @urgency,
            'short': true,
          },
        ],
      },
    ]
  })

SELECT * FROM slackbot.open_dialog
  WHERE $body=(SELECT {
    'dialog' : {
      'title': 'Submit a helpdesk ticket',
      'callback_id': 'submit-ticket',
      'submit_label': 'Submit',
      'elements': [
        {
          'label': 'Title',
          'type': 'text',
          'name': 'title',
          'value': @text,
          'hint': '30 second summary of the problem',
        },
        {
          'label': 'Description',
          'type': 'textarea',
          'name': 'description',
          'optional': true,
        },
        {
          'label': 'Urgency',
          'type': 'select',
          'name': 'urgency',
          'options': [
            { 'label': 'Low', 'value': 'Low' },
            { 'label': 'Medium', 'value': 'Medium' },
            { 'label': 'High', 'value': 'High' },
          ],
        },
      ]
    },
    'trigger_id' : @trigger_id
  })

SELECT * FROM airtable.create_record
  WHERE baseId=@baseId		-- Be sure to set this to your Airtable BaseId in environment variables
  AND table='Tickets'
  AND $body=(SELECT {
    'fields' : {
             	'userId': @id,
             	'userEmail': @email,
             	'title': @title,
             	'description': @description,
             	'urgency': @urgency
               },
  })
